import React, { useState } from 'react';
import InputNumber from './InputNumber';
import GenderSelector from './GenderSelector';
import NameGenerator from './NameGenerator';

function App() {
  const [numNames, setNumNames] = useState(1);
  const [gender, setGender] = useState('male');

  const handleNumNamesChange = (event) => {
    setNumNames(event.target.value);
  };

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  return (
    <div>
      <h1>D&D Name Generator</h1>
      <label>
        Number of Names:{' '}
        <InputNumber value={numNames} onChange={handleNumNamesChange} />
      </label>
      <br />
      <label>
        Gender:{' '}
        <GenderSelector value={gender} onChange={handleGenderChange} />
      </label>
      <br />
      <NameGenerator numNames={numNames} gender={gender} />
    </div>
  );
}

export default App;
