import React, { useState } from 'react';
import maleFirstNames from './maleFirstNames';
import femaleFirstNames from './femaleFirstNames';
import lastNames from './lastNames';

const NameGenerator = ({ selectedGender, selectedRace }) => {
  const [names, setNames] = useState([]);

  const generateName = () => {
    const firstName = selectedGender === 'male' ? getRandomItem(maleFirstNames) : getRandomItem(femaleFirstNames);
    const lastName = getRandomItem(lastNames);
    return { firstName, lastName };
  };

  const generateSingleName = () => {
    setNames([generateName()]);
  };

  const generateMultipleNames = () => {
    const generatedNames = [];
    for (let i = 0; i < 10; i++) {
      generatedNames.push(generateName());
    }
    setNames(generatedNames);
  };

  const getRandomItem = (array) => {
    return array[Math.floor(Math.random() * array.length)];
  };

  return (
    <div>
      <button onClick={generateSingleName}>Generate Single Name</button>
      <button onClick={generateMultipleNames}>Generate 10 Names</button>
      <h2>Generated Names</h2>
      <ul>
        {names.map((name, index) => (
          <li key={index}>{name.firstName} {name.lastName}</li>
        ))}
      </ul>
    </div>
  );
};

export default NameGenerator;
