import React, { useState } from 'react';
import NameGenerator from './NameGenerator';
import GenderSelector from './GenderSelector';
import RaceSelector from './RaceSelector';
import { races, cultures } from './data';

function App() {
  const [selectedGender, setSelectedGender] = useState('male'); // Default to male
  const [selectedRace, setSelectedRace] = useState('Human'); // Default to Human
  const [selectedCulture, setSelectedCulture] = useState('Human'); // Default to Human

  const handleGenderChange = (newGender) => {
    setSelectedGender(newGender);
  };

  const handleRaceChange = (newRace) => {
    setSelectedRace(newRace);
  };

  const handleCultureChange = (newCulture) => {
    setSelectedCulture(newCulture);
  };

  return (
    <div>
      <h1>D&D Character Name Generator</h1>
      <GenderSelector onChange={handleGenderChange} />
      <RaceSelector options={races} onChange={handleRaceChange} />
      <RaceSelector options={cultures} onChange={handleCultureChange} />
      <NameGenerator 
        selectedGender={selectedGender} 
        selectedRace={selectedRace} 
        selectedCulture={selectedCulture} 
      />
    </div>
  );
}

export default App;
