import React, { useState } from 'react';
import NameGenerator from './NameGenerator';
import GenderSelector from './GenderSelector';
import Selector from './Selector';
import races from './races';
import cultures from './cultures';
import femaleFirstNames from './femaleFirstNames';
import maleFirstNames from './maleFirstNames';
import lastNames from './lastNames';

function App() {
  const [selectedGender, setSelectedGender] = useState('male');
  const [selectedRace, setSelectedRace] = useState('Human');
  const [selectedCulture, setSelectedCulture] = useState('Human');
  const [generatedNames, setGeneratedNames] = useState([]);

  const handleGenderChange = (newGender) => {
    setSelectedGender(newGender);
  };

  const handleRaceChange = (newRace) => {
    setSelectedRace(newRace);
  };

  const handleCultureChange = (newCulture) => {
    setSelectedCulture(newCulture);
  };

  const generateName = () => {
    const randomFirstName = Math.floor(Math.random() * femaleFirstNames.length);
    const randomLastName = Math.floor(Math.random() * lastNames.length);

    const generatedName = {
      firstName: selectedGender === 'male' ? maleFirstNames[randomFirstName] : femaleFirstNames[randomFirstName],
      lastName: lastNames[randomLastName],
    };

    setGeneratedNames([generatedName]);
  };

  const generateNames = () => {
    const generatedNames = [];

    for (let i = 0; i < 10; i++) {
      const randomFirstName = Math.floor(Math.random() * femaleFirstNames.length);
      const randomLastName = Math.floor(Math.random() * lastNames.length);

      const generatedName = {
        firstName: selectedGender === 'male' ? maleFirstNames[randomFirstName] : femaleFirstNames[randomFirstName],
        lastName: lastNames[randomLastName],
      };

      generatedNames.push(generatedName);
    }

    setGeneratedNames(generatedNames);
  };

  return (
    <div>
      <h1>D&D Character Name Generator</h1>
      <GenderSelector onChange={handleGenderChange} />
      <Selector options={races} label="Race" onChange={handleRaceChange} />
      <Selector options={cultures} label="Culture" onChange={handleCultureChange} />
      <NameGenerator 
        selectedGender={selectedGender} 
        selectedRace={selectedRace} 
        selectedCulture={selectedCulture} 
        generateName={generateName} 
        generateNames={generateNames} 
      />
      <div>
        <h2>Generated Names:</h2>
        <ul>
          {generatedNames.map((name, index) => (
            <li key={index}>{name.firstName} {name.lastName}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
